              mriClientId && setCookie("mriClientId", mriClientId, 365);
              mriClientId && setCookie("mriClientIdSetDate", curDate);
            }
          }
          mriSessionId && setCookie("mriSessionId", mriSessionId, 0.0208333)
      }


